
var formats = {
    hex : "0123456789abcdef",
}

module.exports.random_string = function (length, format) {
    if (formats[format] == undefined) format = 'hex';

    format = formats[format];

    var string = "";

    if (typeof length !== 'number') {
        throw new Error('random string requires number for length');
    }

    while (string.length < length) {
        string += format[Math.floor(Math.random() * format.length)];
    }

    return string;
}
