module.exports.File = File;
module.exports.is = is;
module.exports.serializable = serializable;

module.exports.server = require(__dirname + '/server');

// TODO: probably use weak map
var meta = new Map();

function File(url) {
    meta.set(this, {
        url : url
    });
}

function is(value) {
    return meta.has(value);
}

function serializable(file) {
    //  TODO: don't expose meta
    return meta.get(file);
}
