$(function () {
    var uploader = $('<input type="file">');
    var progress_element = $('<progress>');
    progress_element.attr('max', 1);
    uploader.upload({
        progress : function (progress) {
            if (progress == 0) {
                 uploader.replaceWith(progress_element);
            }
            else if (progress == 1) {
                
            }
            progress_element.attr('value', progress);
            console.log(progress);
        },
        accept : {
            type : 'image',
            subtype : '*'
        }
    }, function (error, download_URL) {
        console.log(error, download_URL);
    });
    $('body').append(uploader);
});
