
module.exports = get_persistant_proxy;

var meta = require("./meta");
var get_type = require("./get_type");
var get_time = require("./get_time");
var send_to_watching = require("./send_to_watching");
var records = require("./records");
var proxies = require('./proxies');
var events = require('./events');
var scoped = require('./scoped');
var serializable_from_instance = require('./serializable_from_instance');
var create = require('./create');
var auth = require('auth');

function get_persistant_proxy(id) {

    var hash = {};

    var permissions;

    function set_field(target, field, val, receiver) {
        if (meta.get(proxy).field_settings.frozen[field] === true) {
            throw new Error('"' + field + '" is not editable');
        }
        if (meta.get(proxy).loaded) {
            auth.authorize('set', permissions, field, val);
        }

        var type = get_type(val);
//        var persistant = meta.has(val)

        if (type === undefined && typeof val == 'object') {
            var old = val;
            val = create('hashmap');
            old.id = meta.get(val).id;
            type = 'hashmap';
        }

        //  type will be undefined for a loading persistant object
        //  checking meta ensures persistant objects are allowed
        if (type) {
            var time = get_time();
            var operation = 'set';
            if (meta.get(proxy).loaded && meta.get(proxy).backup) {
                records.save({
                    time      : time,
                    id        : id,
                    operation : operation,
                    field     : field,
                    value     : serializable_from_instance(val, [{name: id, type : 'reference'}])
                });
            }
            if (val !== undefined) hash[field] = val;
            else delete hash[field];

            if (field == 'permissions') {
                permissions = val;
            }

            var reference = [
                {name : id, type : 'reference'},
                {name : field, type : 'reference'}
            ];
            //  send to watching this field
            send_to_watching(null, reference, val);
            //  send to watching this other proxy
            send_to_watching(null, [{name:id, type : 'reference'}], proxy);
        }
        else {
            throw new Error((typeof val) +  ' not supported in persistant object');
        }
    }

    var proxy = Proxy.create({
        getOwnPropertyDescriptor : function (target, name) {
            return hash[name];
        },
        getOwnPropertyNames : function (target) {
            var properties = [];
            for (var property in hash) {
                if (hash.hasOwnProperty(property)) {
                    properties.push(property);
                }
            }
            return properties;
        },
        delete : function (name) {
            proxy[name] = undefined;
        },
        freeze : function () {},
        seal : function () {},
        preventExtensions : function () {},
        has : function () {},
        hasOwn : function () {},
        get : function (target, name, receiver) {
            if (meta.get(proxy).loaded) {
                auth.authorize('get', permissions, name);
            }
            return hash[name];
        },
        set : set_field,
        enumerate : function (target) {
            var properties = [];
            for (var property in hash) {
                if (hash.hasOwnProperty(property)) {
                    properties.push(property);
                }
            }
            return properties;
        },
        keys : function (target) {
            var properties = [];
            for (var property in hash) {
                if (hash.hasOwnProperty(property)) {
                    properties.push(property);
                }
            }
            return properties;
        },
        apply : function () {},
        construct : function () {}
    });

    meta.set(proxy, {
        id              : id,
        hash            : hash,
        loaded          : false,
        unloading       : false,
        field_settings  : {
            frozen : {}
        },
        pending_records : [],
        dependencies    : [],
        onload          : [],
    });

    proxies[id] = proxy;

    events.trigger('control', id);

    return proxy;
}

