var meta = require('../meta');

module.exports.freeze = freeze;
module.exports.unfreeze = unfreeze;

function unfreeze(persistant, field) {
    var data = meta.get(persistant);
    if (data) {
        delete data.field_settings.frozen[field];
    }
    else {
        throw new Error('cannot unfreeze field on non persistant object');
    }
}

function freeze(persistant, field) {
    var data = meta.get(persistant);
    if (data) {
        data.field_settings.frozen[field] = true;
    }
    else {
        throw new Error('cannot freeze field on non persistant object');
    }
}
