var persist = require('persist');
var context = {};

var passed = 0;
var failed = 0;

var assert = function (condition, task) {
    if (condition) {
        passed++;
        console.log('    PASS', task);
    }
    else {
        failed++;
        console.log('    FAIL', task);
    }
}

var a = persist.create('map');
var id = persist.identify(a);
persist.unload(a);

persist.load(id, {}, function (err, a) {
    console.log(1, err, a);
});

persist.load(id, {}, function (err, a) {
    console.log(2, err, a)
});


