var fs = require('fs');


var local = __dirname + '/local/';

module.exports.get = function (key, callback) {
    fs.readFile(local + key, callback);
}

module.exports.append = function (key, data, callback) {
    fs.appendFile(local + key, data, callback);
}

module.exports.exists = function (key, callback) {
    fs.exists(local + key, callback);
}
